import React from 'react'
import axios from 'axios'
import styles from './App.module.scss'

class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			projects: []
		}
	}

	getData = () => {
		let self = this
		axios.get('/projects')
			.then(function (response) {
				// handle success
				console.log(response.data);
				self.setState({
					projects: response.data
				})
			})
			.catch(function (error) {
				// handle error
				//{JSON.stringify(this.state.data)}
				console.log(error);
			});
	}

	componentDidMount = () => {
		this.getData();
	}

	render() {
		return (
			<>

				{this.state.projects.map((project, i) =>
					<div key={i} className={styles.project_wrapper}>

						<nav class="navbar navbar-expand-lg navbar-light bg-light">
							<a class="navbar-brand" href="#">Navbar w/ text</a>
							<button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarText" aria-controls="navbarText" aria-expanded="false" aria-label="Toggle navigation">
								<span class="navbar-toggler-icon"></span>
							</button>
							<div class="collapse navbar-collapse" id="navbarText">
								<ul class="navbar-nav mr-auto">
									<li class="nav-item active">
										<a class="nav-link" href="#">Home <span class="sr-only">(current)</span></a>
									</li>
									<li class="nav-item">
										<a class="nav-link" href="#">Projects</a>
									</li>
									<li class="nav-item">
										<a class="nav-link" href="#">Pricing</a>
									</li>
								</ul>
								<span class="navbar-text">
								<button type="button" class="btn btn-warning">E</button>
    							</span>
								<span class="navbar-text">
									<button type="button" class="btn btn-danger">D</button>
    							</span>
							</div>
						</nav>

						<div>{project.id}: {project.name}</div>

						<div class="input-group mb-3">
							<input type="text" class="form-control" placeholder="Enter here the task you want to add.." ibedby="basic-addon2" />
							<div class="input-group-append">
								<button class="btn btn-outline-secondary" type="button">Add Task</button>
							</div>
						</div>

						<div>
							<input type="checkbox" name="task1" value="task1"></input>
						</div>
						<div>task creator</div>
						<div>task list</div>


					</div>

				)}

			</>
		)
	}
}

export default App
