import React from 'react'
import axios from 'axios'
import styles from './App.module.scss'

class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			projects: []
		}
	}

	getData = () => {
		let self = this
		axios.get('/projects')
			.then(function (response) {
				// handle success
				console.log(response.data);
				self.setState({
					projects: response.data
				})
			})
			.catch(function (error) {
				// handle error
				//{JSON.stringify(this.state.data)}
				console.log(error);
			});
	}

	componentDidMount = () => {
		this.getData();
	}

	render() {
		return (
			<>

				{this.state.projects.map((project, i) =>
					<div class="row mb-5 text-white">
						<div key={i} class="col-md-4 p-3 mx-auto border border-secondary">

							<div class="container bg-dark">
								<div class="row">
									<div class="col-md-10">
										{project.id}: {project.name}
    							</div>
									<div class="col-md-2 p-1 text-center">
										<button type="button" class="btn btn-warning mr-2">E</button>
										<button type="button" class="btn btn-danger">D</button>
									</div>
								</div>
							</div>

							<div>
								<div class="input-group mb-3">
									<input type="text" class="form-control" placeholder="Enter here the task you want to add.." ibedby="basic-addon2" />
									<div class="input-group-append">
										<button class="btn btn-success" type="button">Add Task</button>
									</div>
								</div>
							</div>
							<div class="container">
								<div class="row bg-white">
									<div class="col-sm-1 border p-2 text-center">
										<input type="checkbox" />
									</div>
									<div class="col-sm border ">
										Task name..
    						</div>
									<div class="col-md-2 border p-1 text-center">
										<button type="button" class="btn btn-warning mr-2">E</button>
										<button type="button" class="btn btn-danger">D</button>
									</div>
								</div>
								<div class="row bg-white">
									<div class="col-md-1 border p-2 text-center">
										<input type="checkbox" />
									</div>
									<div class="col-md-9 border ">
										Task name2..
    						</div>
									<div class="col-md-2 border p-1 text-center">
										<button type="button" class="btn btn-warning mr-2">E</button>
										<button type="button" class="btn btn-danger">D</button>
									</div>
								</div>
							</div>

						</div>
					</div>
				)}

			</>
		)
	}
}

export default App
