import React from 'react'
import axios from 'axios'
import styles from './App.module.scss'

class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			projects: []
		}
	}

	getData = () => {
		let self = this
		axios.get('/projects')
			.then(function (response) {
				// handle success
				console.log(response.data);
				self.setState({
					projects: response.data
				})
			})
			.catch(function (error) {
				// handle error
				//{JSON.stringify(this.state.data)}
				console.log(error);
			});
	}

	componentDidMount = () => {
		this.getData();
	}

	render() {
		return (
			<>

				{this.state.projects.map((project, i) =>
					<div key={i} className={styles.project_wrapper}>
						<div>{project.id}: {project.name}</div>

						<div class="input-group">
							<input type="text" class="form-control" placeholder="Recipient's username" aria-label="Recipient's username with two button addons" aria-describedby="button-addon4" />
							<div class="input-group-append" id="button-addon4">
								<button class="btn btn-outline-secondary" type="button">Button</button>
								<button class="btn btn-outline-secondary" type="button">Button</button>
							</div>
						</div>

						<div>
							<input type="checkbox" name="task1" value="task1"></input>
						</div>
						<div>task creator</div>
						<div>task list</div>

						<div class="input-group mb-3">
							<input type="text" class="form-control" placeholder="Enter here the task you want to add.." ibedby="basic-addon2" />
							<div class="input-group-append">
								<button class="btn btn-outline-secondary" type="button">Add Task</button>
							</div>
						</div>
					</div>

				)}

			</>
		)
	}
}

export default App
