import { GET_PROJECTS, ADD_PROJECT, TOGGLE_TODO, DELETE_PROJECT } from '../actions/actionTypes'

function projectsReducer(state = [], action) 
{
    switch(action.type) {
        case GET_PROJECTS:
            return action.projects;

        case ADD_PROJECT:
            return [
                ...state,
                {
                    name: action.name,
                }
            ];

        case TOGGLE_TODO:
            return state.map(todo => (todo.id === action.index) 
                  ? {...todo, done: !todo.done}
                  : todo
            );

        case DELETE_PROJECT:
            return state.filter(todo => todo.id !== action.index);

        default:
            return state;    
    }
}

export default projectsReducer
